-- select 문자열, 수학 함수 Exam

-- exam 테이블에서 평균을 계산하여 수우미양가를 출력하세요 - teacher
select exam.*,
kor + eng + mat as tot,
(kor + eng + mat) / 3 as avg,
substr('가가가가가가양미우수수', trunc(((kor + eng + mat) / 3) / 10)+1 , 1) as grade
from exam;
